mapper.select:
SELECT COUNT(*)
FROM discussion
WHERE book_isbn = #{isbn}

SELECT REVIEW.*, USER.username AS user_name
FROM (
SELECT * FROM review
WHERE book_isbn = #{isbn}


        ORDER BY rate DESC


        ORDER BY rate


        ORDER BY updated_at DESC


LIMIT #{pageInfo.size} OFFSET #{pageInfo.offset}
) REVIEW
INNER JOIN user USER ON REVIEW.user_id = USER.id
