mapper.select:
SELECT (`like` + `unlike`) AS totalVotes
FROM discussion_comment
WHERE id = #{commentId}

SELECT user_id
FROM discussion_comment
WHERE id = #{commentId}

SELECT COUNT(*)
FROM discussion_comment
WHERE discussion_id = #{discussionId}

SELECT dc.id AS comment_id, dc.content AS comment_content, dc.created_at AS comment_created_at, dc.like_count AS like_count, dc.dislike_count AS dislike_count, u.id AS user_id, u.name AS user_name FROM discussion_comment dc INNER JOIN user u ON dc.user_id = u.id WHERE dc.discussion_id = #{discussionId}
ORDER BY dc.created_at DESC
ORDER BY dc.like_count DESC
ORDER BY dc.dislike_count DESC
ORDER BY dc.created_at DESC
LIMIT #{pageInfo.size} OFFSET #{pageInfo.offset}

SELECT COUNT(*)
FROM discussion_comment
WHERE discussion_id = #{discussionId}
