mapper.update:
UPDATE loan
SET status = #{status}
WHERE id = #{id}

mapper.select:
SELECt book.copies_avilable
FROM book
WHERE isbn = #{bookIsbn}

mapper.update:
UPDATE book
SET copies_avilable = copies_avilable - 1
WHERE isbn = #{bookIsbn} AND copies_avilable > 0;

UPDATE book
SET copies_avilable = copies_avilable + 1
WHERE  isbn = #{bookIsbn}
