mapper.update:
UPDATE user
SET name = #{name}, password = #{password}, email = #{email}, updated_at = DEFAULT
WHERE id = #{id}

mapper.select:
SELECT a.*
FROM review a
WHERE a.user_id = #{userId}

SELECT user_id FROM discussion_comment
WHERE discussion_id = #{discussionId}
ORDER BY `like` DESC LIMIT 1

SELECT EXISTS (SELECT 1 FROM point_grant_history WHERE user_id = #{userId}
                                                 AND reason = #{reason}
                                                 AND DATE(grant_date) = CURDATE())
